        /// <summary>
        /// 
        /// </summary>
        /// <param name="currentCemuVersion"></param>
        private void SetOffsets(string currentCemuVersion)
        {
            Tuple<int[], int[]> settingsOffsets;
            if (settingsByVersion.TryGetValue(currentCemuVersion.Replace("a", "").Replace("b", "").Replace("c", "").Replace("d", "").Replace("e", "").Replace("f", "").Replace("g", ""), out settingsOffsets))
            {
                SettingsFile = settingsOffsets.Item1;
                SettingsOffsets = settingsOffsets.Item2;
            }
            else
            {
                SettingsFile = V160_Settings;
                SettingsOffsets = settings_1_6_0_bin;
            }
        }

        /// <summary>
        /// 
        /// </summary>
        /// <returns></returns>
        InstalledVersion GetVersion()
        {
            InstalledVersion version = null;
            if (settings != null)
            {
                if (settings.PreferedVersion == "Latest")
                {
                    version = model.Settings.InstalledVersions.FirstOrDefault(v => v.IsLatest);
                }
                else
                {
                    version = model.Settings.InstalledVersions.FirstOrDefault(v => v.Name == settings.PreferedVersion);
                }
            }
            return version;
        }

        /// <summary>
        /// 
        /// </summary>
        internal void WriteSettingsBinFile()
        {
            InstalledVersion version = GetVersion();
          

            if (version != null)
            {
                SetOffsets(version.Version.Replace("cemu_", ""));
                if (version.Version.Contains("1.10.0"))
                {
                    settings.Volume = 80;
                }

                if (File.Exists(version.Folder + "\\settings.bin"))
                {
                    File.Delete(version.Folder + "\\settings.bin");
                }

                // No settings file, so lets build our own..
                if (!File.Exists(version.Folder + "\\settings.bin"))
                {
                    using (FileStream fn = new FileStream(version.Folder + "\\settings.bin", FileMode.Create, FileAccess.ReadWrite))
                    {
                        foreach (int file in SettingsFile)
                        {
                            fn.WriteByte((byte)file);
                        }
                    }
                }

                FileManager.GrantAccess(version.Folder + "\\settings.bin");

                if (information != null)
                {
                    WriteSettings(version.Folder);
                    WriteGraphicsPacks(version.Folder);
                }
            }
        }

        /// <summary>
        /// 
        /// </summary>
        /// <param name="folder"></param>
        void WriteSettings(string folder)
        {
            if (!Directory.Exists(folder + "\\gameProfiles\\"))
            {
                Directory.CreateDirectory(folder + "\\gameProfiles\\");
            }

            CurrentUserSecurity cs = new CurrentUserSecurity();

            if (cs.HasAccess(new FileInfo(folder + "\\gameProfiles\\" + information.TitleId + ".ini"), System.Security.AccessControl.FileSystemRights.Write))
            {
                using (StreamWriter writer = new StreamWriter(folder + "\\gameProfiles\\" + information.TitleId + ".ini"))
                {
                    writer.WriteLine("[Graphics]");
                    writer.WriteLine("accurateShaderMul = " + (settings.AccaccurateShaderMul == 1 ? "true" : "false"));
                    writer.WriteLine("disableGPUFence =  " + (settings.DisableGPUFence == 1 ? "true" : "false"));
                    writer.WriteLine("[CPU]");
                    writer.WriteLine("emulateSinglePrecision = " + (settings.EmulateSinglePrecision == 1 ? "true" : "false"));
                }
            }

            FileManager.GrantAccess(folder + "\\gameProfiles\\" + information.TitleId + ".ini");

            if (cs.HasAccess(new FileInfo(folder + "\\settings.bin"), System.Security.AccessControl.FileSystemRights.Write))
            {
                using (FileStream fn = new FileStream(folder + "\\settings.bin", FileMode.Open, FileAccess.ReadWrite))
                {
                    WriteByte(fn, DebugGX2ApiOffset, settings.DebugGX2ApiOffset);
                    WriteByte(fn, DebugUnsupportedAPICallsOffset, settings.DebugUnsupportedAPICallsOffset);
                    WriteByte(fn, DebugThreadSynchronisationAPIOffset, settings.DebugThreadSynchronisationAPIOffset);
                    WriteByte(fn, DebugAudioAPIOffset, settings.DebugAudioAPIOffset);
                    WriteByte(fn, DebugInputAPIOffset, settings.DebugInputAPIOffset);

                    WriteByte(fn, VolumeOffset, settings.Volume);
                    WriteByte(fn, EnableDebugOffset, settings.EnableDebugOffset);
                    WriteByte(fn, CpuModeOffset, (byte)settings.CpuMode);
                    WriteByte(fn, CpuTimerOffset, (byte)settings.CpuTimer);

                    WriteByte(fn, SettingsOffsets[(int)Settings.GpuBufferAccuractOffset], (byte)settings.GpuBufferCacheAccuracy);
                    WriteByte(fn, SettingsOffsets[(int)Settings.UpscaleFilterOffset], (byte)settings.UpscaleFilter);
                    WriteByte(fn, SettingsOffsets[(int)Settings.FullScreenScalingOffset], (byte)settings.FullScreenScaling);
                    WriteByte(fn, SettingsOffsets[(int)Settings.VSyncOffset], settings.EnableVSync);

                    WriteByte(fn, SettingsOffsets[(int)Settings.RenderUpsideDownOffset], settings.RenderUpsideDown);
                    WriteByte(fn, SettingsOffsets[(int)Settings.DisableAudioOffset], settings.DisableAudio);
                    WriteByte(fn, SettingsOffsets[(int)Settings.ConsoleRegionOffset], (byte)model.Settings.ConsoleRegion);
                    WriteByte(fn, SettingsOffsets[(int)Settings.ConsoleLanguageOffset], (byte)model.Settings.ConsoleLanguage);
                    WriteByte(fn, SettingsOffsets[(int)Settings.BoTWWorkAroundOffset], settings.EnableBoTWCrashWorkaround);
                    WriteByte(fn, SettingsOffsets[(int)Settings.FullSyncAtGx2Offset], settings.FullSyncAtGX2DrawDone);
                    WriteByte(fn, SettingsOffsets[(int)Settings.SeparateGamepadView], settings.SeparateGamePadView);
                }
            }
        }

        /// <summary>
        /// 
        /// </summary>
        /// <param name="fn"></param>
        /// <param name="position"></param>
        /// <param name="value"></param>
        private static void WriteByte(FileStream fn, int position, int value)
        {
            if (position != 0)
            {
                fn.Seek(position, SeekOrigin.Begin);
                fn.WriteByte((byte)value);
            }
        }
        
        /// <summary>
        /// 
        /// </summary>
        void WriteGraphicsPacks(string folder)
        {
            EnableDefaultGraphicsPack();
            int packs = 0;
            foreach (var pack in settings.graphicsPacks)
            {
                if (pack.Active)
                {
                    //File.Copy("")
                    FileManager.CopyFilesRecursively(new DirectoryInfo("graphicsPacks\\graphicsPacks\\" + pack.Folder), new DirectoryInfo(folder + "\\graphicPacks"));
                    packs++;
                }
            }

            if (packs > 0)
            {
                int gfxPackStartOffset;
                using (FileStream fn = new FileStream(folder + "\\settings.bin", FileMode.Open, FileAccess.ReadWrite))
                {
                    fn.Seek(0x23, SeekOrigin.Begin);
                    gfxPackStartOffset = fn.ReadByte();
                    fn.Seek(0x23, SeekOrigin.Begin);
                    fn.WriteByte((byte)(gfxPackStartOffset + (9 * packs)));

                    gfxPackStartOffset += 0x23 + 4;
                    fn.Seek(gfxPackStartOffset - 4, SeekOrigin.Begin);
                    fn.WriteByte((byte)packs);
                }

                packIndex = 0;
                foreach (var pack in settings.graphicsPacks)
                {
                    AppendGraphicsPack(folder, gfxPackStartOffset, pack);
                }

                if (resolutionPack != null)
                {
                    AppendGraphicsPack(folder, gfxPackStartOffset, resolutionPack);
                }

                using (FileStream fn = new FileStream(folder + "\\settings.bin", FileMode.Open, FileAccess.ReadWrite))
                {
                    fn.Seek(gfxPackStartOffset + (9 * packs), SeekOrigin.Begin);
                    for (int i = 0; i < 137; ++i)
                    {
                        fn.WriteByte(0);
                    }
                }
            }
        }

        /// <summary>
        /// 
        /// </summary>
        void EnableDefaultGraphicsPack()
        {
            foreach (var pack in settings.graphicsPacks)
            {
                if (IsResolutionPack(pack.Title))
                {
                    if (pack.Title.Contains(model.Settings.DefaultResolution))
                    {
                        resolutionPack = new GraphicsPack()
                        {
                            Active = true,
                            File = pack.File,
                            Folder = pack.Folder,
                            Title = pack.Title,
                            Gui = pack.Gui
                        };
                        break;
                    }
                }
            }
        }

        /// <summary>
        /// 
        /// </summary>
        /// <param name="title"></param>
        /// <returns></returns>
        private bool IsResolutionPack(string title)
        {
            if (title != null)
            {
                foreach (var resolution in supportedResolutions)
                {
                    if (title.Contains(resolution))
                    {
                        return true;
                    }
                }
            }
            return false;
        }

        /// <summary>
        /// 
        /// </summary>
        /// <param name="hex"></param>
        /// <returns></returns>
        public static byte[] StringToByteArray(String hex)
        {
            int NumberChars = hex.Length;
            byte[] bytes = new byte[NumberChars / 2];
            for (int i = 0; i < NumberChars; i += 2)
            {
                bytes[i / 2] = Convert.ToByte(hex.Substring(i, 2), 16);
            }
            return bytes;
        }

        /// <summary>
        /// 
        /// </summary>
        /// <param name="gfxPackStartOffset"></param>
        /// <param name="pack"></param>
        /// <param name="folder"></param>
        void AppendGraphicsPack(string folder, int gfxPackStartOffset, GraphicsPack pack)
        {
            if (resolutionPack != null)
            {
                if (pack.Title != resolutionPack.Title)
                {
                    if (IsResolutionPack(pack.Title))
                    {
                        return;
                    }
                }
            }

            using (FileStream fn = new FileStream(folder + "\\settings.bin", FileMode.Open, FileAccess.ReadWrite))
            {
                if (pack.Active)
                {
                    if (pack.Gui != null)
                    {

                        fn.Seek(gfxPackStartOffset + (packIndex++ * 9), SeekOrigin.Begin);
                        fn.Write(StringToByteArray(pack.Gui), 0, 8);
                        fn.WriteByte(1);
                        CopyGraphicsPack(folder, pack);
                    }
                }
            }
        }

        /// <summary>
        /// 
        /// </summary>
        /// <param name="folder"></param>
        /// <param name="pack"></param>
        private static void CopyGraphicsPack(string folder, GraphicsPack pack)
        {
            if (!Directory.Exists(folder + "\\graphicPacks\\" + pack.Folder))
            {
                Directory.CreateDirectory(folder + "\\graphicPacks\\" + pack.Folder);
            }
            FileManager.CopyFilesRecursively(new DirectoryInfo("graphicsPacks\\graphicsPacks\\" + pack.Folder), new DirectoryInfo(folder + "\\graphicPacks\\" + pack.Folder));
        }
    }
}
